<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Ad;
/**
 * AdRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AdRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAll()
    {
        return $this->findBy(array(), array('dateCreated' => 'DESC'));
    }

    public function findbyCategory(int $category) : array
    {
        $qb = $this->createQueryBuilder(Ad::class);
        $res = $qb->SELECT('a')
            ->FROM('AppBundle\Entity\Ad', 'a')
            ->JOIN('a.category', 'c')
            ->WHERE('c.id = ?1')
            ->ORDERBY('a.dateCreated', 'DESC')
            ->setParameter(1, $category)
            ->getQuery()->getResult();
        return $res;
    }

    public function findByKeyWord(string $input) : array
    {
        $qb = $this->createQueryBuilder(Ad::class);
        $res = $qb->SELECT('a')
            ->FROM('AppBundle\Entity\Ad', 'a')
            ->JOIN('a.category', 'c')
            ->WHERE('a.title LIKE :input 
                    OR a.description LIKE :input 
                    OR c.name LIKE :input 
                    OR a.city LIKE :input 
                    OR a.zipCode LIKE :input')
            ->ORDERBY('a.dateCreated', 'DESC')
            ->setParameters([
                'input' => '%'.$input.'%'
            ])
            ->getQuery()->getResult();
        return $res;
    }
}
